Branch User::getBranch(const DbClientPtr &clientPtr) const {
    static const std::string sql = "select * from branch where manager_id = ?";
    Result r(nullptr);
    {
        auto binder = *clientPtr << sql;
        binder << *userId_ << Mode::Blocking >>
            [&r](const Result &result) { r = result; };
        binder.exec();
    }
    if (r.size() == 0)
    {
        throw UnexpectedRows("0 rows found");
    }
    else if (r.size() > 1)
    {
        throw UnexpectedRows("Found more than one row");
    }
    return Branch(r[0]);
}

void User::getBranch(const DbClientPtr &clientPtr,
                     const std::function<void(Branch)> &rcb,
                     const ExceptionCallback &ecb) const
{
    static const std::string sql = "select * from branch where manager_id = ?";
    *clientPtr << sql
               << *userId_
               >> [rcb = std::move(rcb), ecb](const Result &r){
                    if (r.size() == 0)
                    {
                        ecb(UnexpectedRows("0 rows found"));
                    }
                    else if (r.size() > 1)
                    {
                        ecb(UnexpectedRows("Found more than one row"));
                    }
                    else
                    {
                        rcb(Branch(r[0]));
                    }
               }
               >> ecb;
}
